    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       // Fetch data from the database based on provided username
        String username = JOptionPane.showInputDialog("Enter Username:");
        if (username != null && !username.isEmpty()) {
            try {
                Connection conn = DriverManager.getConnection("jdbc:mysql://localhost/edu", "root", "@2003Moon2003");
                String query = "SELECT * FROM edu WHERE Username=?";
                PreparedStatement pstmt = conn.prepareStatement(query);
                pstmt.setString(1, username);
                ResultSet rs = pstmt.executeQuery();
                
                // Check if the username exists in the database
                if (rs.next()) {
                    // Populate JTables with user's information
                    populateMastersTable(rs);
                    populateHonoursTable(rs);
                    populateHscTable(rs);
                    populateSscTable(rs);
                } else {
                    JOptionPane.showMessageDialog(null, "Username not found!");
                }
                
                // Close connections
                rs.close();
                pstmt.close();
                conn.close();
            } catch (SQLException ex) {
                ex.printStackTrace();
            }
        } else {
            JOptionPane.showMessageDialog(null, "Please provide a username!");
        }
  // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

