  private void populateMastersTable(ResultSet rs) throws SQLException {
        DefaultTableModel model = (DefaultTableModel) jTable3.getModel();
        model.setRowCount(0);
        Object[] row = {
            rs.getString("MastersDegree"),
            rs.getString("MastersFrom"),
            rs.getString("MStudentId"),
            rs.getString("MastersCgpa"),
            rs.getString("MastersPassingYear")
        };
        model.addRow(row);
    }

